 //.....................Getting list of Identities and it's size............. 
   List<Identity> identities = context.getObjects(Identity.class);
	 if (identities != null && identities.size() > 0) {
	     for (Identity identity : identities) {
	         System.out.println("Identity Fullname:  " + identity.getName()+"\n"
	                           +"Identity Firstname: " + identity.getFirstname()+"\n"
	                           +"Capabilities:       " + identity.getCapabilities());   

	     }
	     System.out.println("Total Identities: " + identities.size());
	 } else {
	     System.out.println("No identities found.");
	 }	  
	}
