//........List out all active employee job title ends with 'I' or 'II'-...........



Filter first = Filter.like("lastname", "I", Filter.MatchMode.START).or(Filter.like("lastname", "II", Filter.MatchMode.START));
		Filter jobFilter=Filter.eq("inactive", false);	
	QueryOptions qo=new QueryOptions();
